<?php

namespace Discutea\DForumBundle\Repository;

/**
 * ForumRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ForumRepository extends \Doctrine\ORM\EntityRepository
{

    function nombresujet($forum)
    {
        $query=$this->getEntityManager()->createQuery(
            "select COUNT (m.id) as nb from DForumBundle:Forum m WHERE m.category=:p  " )
            ->setParameter('p',$forum);

        return $query->getResult();

    }

}
